
public class Array_Table {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int arrayNums[] = {10,20,30,40,50,60};
		
		System.out.println("Index\tValue");
		for (int counter = 0; counter < arrayNums.length; counter++)
		{
			
			System.out.println(counter + "\t" + arrayNums[counter]);
		}

	}

}
